where 1=1 AND A.DEPT_CODE = b.DEPT_CODE AND b.COLL_CODE= e.COLL_CODE AND e.CAMP_CODE = F.CAMP_CODE AND G.DETL_MAJ_CODE = A.DETL_MAJ_CODE AND A.ADMS_NO = H.ADMS_NO AND A.ADMS_NO = I.ADMS_NO AND A.CORS_TYPE_CODE = I.CORS_TYPE_CODE AND A.DEPT_CODE = I.DEPT_CODE AND IFNULL(A.ARI_INST_CODE,'') = IFNULL(I.ARI_INST_CODE,'') AND A.APPL_NO = #{applNo}
ORDER BY APPL_DATE DESC , APPL_ID DESC

