SELECT count(*)
FROM  APPL a
left outer join ARI_INST d
on a.ARI_INST_CODE = d.ARI_INST_CODE
, DEPT b, COLL e, CAMP F, ADMS H
where 1=1
AND a.DEPT_CODE = b.DEPT_CODE
AND b.COLL_CODE= e.COLL_CODE
AND e.CAMP_CODE = F.CAMP_CODE
AND a.ADMS_NO = H.ADMS_NO

   AND a.APPL_STS_CODE = '00020'

    AND a.ADMS_NO =#{admsNo}


   AND a.APPL_ATTR_CODE =#{applAttrCode}


    AND F.CAMP_CODE =#{campCode}



    AND e.COLL_CODE =#{collCode}


    AND b.DEPT_CODE = #{deptCode}

SELECT DD.DEPT_SEQ AS DEPT_SEQ, DD.DEPT_CODE, DD.CAMP_NAME, DD.COLL_NAME, DD.DEPT_NAME,DD.CAMP_CODE,DD.COLL_CODE, COUNT(CASE WHEN A.CORS_TYPE_CODE IN('01','05','07','10') THEN 1 END) AS CNT1, COUNT(CASE WHEN A.CORS_TYPE_CODE IN('02','06','08','11') THEN 1 END) AS CNT2, COUNT(CASE WHEN A.CORS_TYPE_CODE IN('03','04','12') THEN 1 END) AS CNT3, COUNT(CASE WHEN A.CORS_TYPE_CODE IN(09) THEN 1 END) AS CNT4, COUNT( A.CORS_TYPE_CODE ) AS TOTAL_CNT FROM ( SELECT BO.ADMS_TYPE ADMS_TYPE, BO.ADMS_NO ADMS_NO, BO.DEPT_CODE DEPT_CODE, BA.DEPT_NAME DEPT_NAME, BA.DEPT_SEQ DEPT_SEQ, BB.COLL_CODE COLL_CODE, BB.COLL_NAME COLL_NAME, BC.CAMP_CODE CAMP_CODE, BC.CAMP_NAME CAMP_NAME FROM ( SELECT CA.ADMS_NO ADMS_NO, CA.ENTR_YEAR ENTR_YEAR, CA.ADMS_TYPE ADMS_TYPE, CB.DEPT_CODE DEPT_CODE FROM ADMS CA, ADMS_CORS CB WHERE CA.ADMS_NO =CB.ADMS_NO GROUP BY CB.DEPT_CODE, CB.ADMS_NO ) BO, DEPT BA, COLL BB, CAMP BC WHERE BO.DEPT_CODE =BA.DEPT_CODE AND BA.COLL_CODE = BB.COLL_CODE AND BB.CAMP_CODE = BC.CAMP_CODE
AND BO.ADMS_NO =#{admsNo}
AND BC.CAMP_CODE =#{campCode}
AND BB.COLL_CODE =#{collCode}
AND BO.DEPT_CODE = #{deptCode}
) DD LEFT OUTER JOIN ( SELECT * FROM APPL WHERE APPL_STS_CODE = '00020'
) A on A.DEPT_CODE = DD.DEPT_CODE and A.ADMS_NO =DD.ADMS_NO GROUP BY DD.DEPT_CODE ORDER BY DD.CAMP_CODE,DD.COLL_CODE, DD.DEPT_SEQ

SELECT DD.DEPT_SEQ AS DEPT_SEQ, DD.DEPT_CODE, DD.CAMP_NAME, DD.COLL_NAME, DD.DEPT_NAME,DD.CAMP_CODE,DD.COLL_CODE, COUNT(CASE WHEN A.CORS_TYPE_CODE IN('01') THEN 1 END) AS CNT1, COUNT(CASE WHEN A.CORS_TYPE_CODE IN('02') THEN 1 END) AS CNT2, COUNT(CASE WHEN A.CORS_TYPE_CODE IN('03') THEN 1 END) AS CNT3, COUNT(CASE WHEN A.CORS_TYPE_CODE IN('04') THEN 1 END) AS CNT4, COUNT(CASE WHEN A.CORS_TYPE_CODE IN('05') THEN 1 END) AS CNT5, COUNT(CASE WHEN A.CORS_TYPE_CODE IN('06') THEN 1 END) AS CNT6, COUNT(CASE WHEN A.CORS_TYPE_CODE IN('07') THEN 1 END) AS CNT7, COUNT(CASE WHEN A.CORS_TYPE_CODE IN('08') THEN 1 END) AS CNT8, COUNT(CASE WHEN A.CORS_TYPE_CODE IN('09') THEN 1 END) AS CNT9, COUNT(CASE WHEN A.CORS_TYPE_CODE IN('10') THEN 1 END) AS CNT10, COUNT(CASE WHEN A.CORS_TYPE_CODE IN('11') THEN 1 END) AS CNT11, COUNT(CASE WHEN A.CORS_TYPE_CODE IN('12') THEN 1 END) AS CNT12, COUNT( A.CORS_TYPE_CODE ) AS TOTAL_CNT FROM ( SELECT BO.ADMS_TYPE ADMS_TYPE, BO.ADMS_NO ADMS_NO, BO.DEPT_CODE DEPT_CODE, BA.DEPT_NAME DEPT_NAME, BA.DEPT_SEQ DEPT_SEQ, BB.COLL_CODE COLL_CODE, BB.COLL_NAME COLL_NAME, BC.CAMP_CODE CAMP_CODE, BC.CAMP_NAME CAMP_NAME FROM ( SELECT CA.ADMS_NO ADMS_NO, CA.ENTR_YEAR ENTR_YEAR, CA.ADMS_TYPE ADMS_TYPE, CB.DEPT_CODE DEPT_CODE FROM ADMS CA, ADMS_CORS CB WHERE CA.ADMS_NO =CB.ADMS_NO GROUP BY CB.DEPT_CODE, CB.ADMS_NO ) BO, DEPT BA, COLL BB, CAMP BC WHERE BO.DEPT_CODE =BA.DEPT_CODE AND BA.COLL_CODE = BB.COLL_CODE AND BB.CAMP_CODE = BC.CAMP_CODE
AND BO.ADMS_NO =#{admsNo}
AND BC.CAMP_CODE =#{campCode}
AND BB.COLL_CODE =#{collCode}
AND BO.DEPT_CODE = #{deptCode}
) DD LEFT OUTER JOIN ( SELECT * FROM APPL WHERE APPL_STS_CODE = '00020'
) A on A.DEPT_CODE = DD.DEPT_CODE and A.ADMS_NO =DD.ADMS_NO GROUP BY DD.DEPT_CODE ORDER BY DD.CAMP_CODE,DD.COLL_CODE, DD.DEPT_SEQ

SELECT DD.DEPT_SEQ AS DEPT_SEQ, DD.DEPT_CODE, DD.CAMP_NAME, DD.COLL_NAME, DD.DEPT_NAME,DD.CAMP_CODE,DD.COLL_CODE, COUNT(CASE WHEN A.CORS_TYPE_CODE IN('01','05','07','10') THEN 1 END) AS CNT1, COUNT(CASE WHEN A.CORS_TYPE_CODE IN('02','06','08','11') THEN 1 END) AS CNT2, COUNT(CASE WHEN A.CORS_TYPE_CODE IN('03','04','12') THEN 1 END) AS CNT3, COUNT(CASE WHEN A.CORS_TYPE_CODE IN(09) THEN 1 END) AS CNT4, COUNT( A.CORS_TYPE_CODE ) AS TOTAL_CNT FROM ( SELECT BO.ADMS_TYPE ADMS_TYPE, BO.ADMS_NO ADMS_NO, BO.DEPT_CODE DEPT_CODE, BA.DEPT_NAME DEPT_NAME, BA.DEPT_SEQ DEPT_SEQ, BB.COLL_CODE COLL_CODE, BB.COLL_NAME COLL_NAME, BC.CAMP_CODE CAMP_CODE, BC.CAMP_NAME CAMP_NAME FROM ( SELECT CA.ADMS_NO ADMS_NO, CA.ENTR_YEAR ENTR_YEAR, CA.ADMS_TYPE ADMS_TYPE, CB.DEPT_CODE DEPT_CODE FROM ADMS CA, ADMS_CORS CB WHERE CA.ADMS_NO =CB.ADMS_NO GROUP BY CB.DEPT_CODE, CB.ADMS_NO ) BO, DEPT BA, COLL BB, CAMP BC WHERE BO.DEPT_CODE =BA.DEPT_CODE AND BA.COLL_CODE = BB.COLL_CODE AND BB.CAMP_CODE = BC.CAMP_CODE
AND BO.ADMS_NO =#{admsNo}
AND BC.CAMP_CODE =#{campCode}
AND BB.COLL_CODE =#{collCode}
AND BO.DEPT_CODE = #{deptCode}
) DD LEFT OUTER JOIN ( SELECT * FROM APPL WHERE APPL_STS_CODE IN ('00010','00021')
) A on A.DEPT_CODE = DD.DEPT_CODE and A.ADMS_NO =DD.ADMS_NO GROUP BY DD.DEPT_CODE ORDER BY DD.CAMP_CODE,DD.COLL_CODE, DD.DEPT_SEQ

SELECT DD.DEPT_SEQ AS DEPT_SEQ, DD.DEPT_CODE, DD.CAMP_NAME, DD.COLL_NAME, DD.DEPT_NAME,DD.CAMP_CODE,DD.COLL_CODE, COUNT(CASE WHEN DATE_FORMAT(APPL_DATE, '%Y%m%d')= DATE_FORMAT(NOW(),'%Y%m%d')-6 THEN 1 END) AS CNT1, COUNT(CASE WHEN DATE_FORMAT(APPL_DATE, '%Y%m%d')= DATE_FORMAT(NOW(),'%Y%m%d')-5 THEN 1 END) AS CNT2, COUNT(CASE WHEN DATE_FORMAT(APPL_DATE, '%Y%m%d')= DATE_FORMAT(NOW(),'%Y%m%d')-4 THEN 1 END) AS CNT3, COUNT(CASE WHEN DATE_FORMAT(APPL_DATE, '%Y%m%d')= DATE_FORMAT(NOW(),'%Y%m%d')-3 THEN 1 END) AS CNT4, COUNT(CASE WHEN DATE_FORMAT(APPL_DATE, '%Y%m%d')= DATE_FORMAT(NOW(),'%Y%m%d')-2 THEN 1 END) AS CNT5, COUNT(CASE WHEN DATE_FORMAT(APPL_DATE, '%Y%m%d')= DATE_FORMAT(NOW(),'%Y%m%d')-1 THEN 1 END) AS CNT6, COUNT(CASE WHEN DATE_FORMAT(APPL_DATE, '%Y%m%d')= DATE_FORMAT(NOW(),'%Y%m%d') THEN 1 END) AS CNT7, COUNT( A.CORS_TYPE_CODE ) AS TOTAL_CNT FROM ( SELECT BO.ADMS_TYPE ADMS_TYPE, BO.ADMS_NO ADMS_NO, BO.DEPT_CODE DEPT_CODE, BA.DEPT_NAME DEPT_NAME, BA.DEPT_SEQ DEPT_SEQ, BB.COLL_CODE COLL_CODE, BB.COLL_NAME COLL_NAME, BC.CAMP_CODE CAMP_CODE, BC.CAMP_NAME CAMP_NAME FROM ( SELECT CA.ADMS_NO ADMS_NO, CA.ENTR_YEAR ENTR_YEAR, CA.ADMS_TYPE ADMS_TYPE, CB.DEPT_CODE DEPT_CODE FROM ADMS CA, ADMS_CORS CB WHERE CA.ADMS_NO =CB.ADMS_NO GROUP BY CB.DEPT_CODE, CB.ADMS_NO ) BO, DEPT BA, COLL BB, CAMP BC WHERE BO.DEPT_CODE =BA.DEPT_CODE AND BA.COLL_CODE = BB.COLL_CODE AND BB.CAMP_CODE = BC.CAMP_CODE
AND BO.ADMS_NO =#{admsNo}
AND BC.CAMP_CODE =#{campCode}
AND BB.COLL_CODE =#{collCode}
AND BO.DEPT_CODE = #{deptCode}
) DD LEFT OUTER JOIN ( SELECT * FROM APPL WHERE APPL_STS_CODE = '00020'
) A on A.DEPT_CODE = DD.DEPT_CODE and A.ADMS_NO =DD.ADMS_NO GROUP BY DD.DEPT_CODE ORDER BY DD.CAMP_CODE,DD.COLL_CODE, DD.DEPT_SEQ

SELECT A.APPL_NO APPL_NO, A.APPL_ID, A.ADMS_NO, A.USER_ID, H.ADMS_TYPE, CODEVAL( 'ADMS_TYPE',H.ADMS_TYPE )ADMS_TYPE_NAME, A.APPL_ATTR_CODE, CODEVAL('APPL_ATTR', A.APPL_ATTR_CODE ) APPL_ATTR_NAME, F.CAMP_NAME, e.COLL_NAME, b.DEPT_CODE, b.DEPT_NAME, A.CORS_TYPE_CODE, CODEVAL( 'CORS_TYPE',A.CORS_TYPE_CODE)CORS_TYPE_NAME , D.ARI_INST_NAME, A.DETL_MAJ_CODE, G.DETL_MAJ_NAME, A.INP_DETL_MAJ, A.KOR_NAME, A.ENG_SUR, A.ENG_NAME, A.RGST_NO, A.RGST_BORN_DATE, A.BORN_DAY, A.CITZ_CNTR_CODE, A.FORN_TYPE_CODE, A.GEND, A.TEL_NUM, A.MOBI_NUM, A.MAIL_ADDR, A.ADDR, A.DETL_ADDR, J.EMER_CONT_CODE, J.EMER_CONT_NAME, J.EMER_CONT_TEL, J.FORL_EXMP_CODE, A.APPL_STS_CODE, CODEVAL( 'APPL_STS',A.APPL_STS_CODE)APPL_STS_NAME , A.APPL_DATE, P.ADMS_FEE, P.PAY_TYPE_CODE, CODEVAL( 'PAY_TYPE',P.PAY_TYPE_CODE) PAY_TYPE_NAME FROM APPL A left outer join APPL_PAY_CS P ON A.APPL_NO = P.APPL_NO left outer join ARI_INST D on A.ARI_INST_CODE = D.ARI_INST_CODE left outer join APPL_GENE J on A.APPL_NO = J.APPL_NO , DEPT b, COLL e, CAMP F, DETL_MAJ G, ADMS H, ADMS_CORS I
where 1=1 AND A.DEPT_CODE = b.DEPT_CODE AND b.COLL_CODE= e.COLL_CODE AND e.CAMP_CODE = F.CAMP_CODE AND G.DETL_MAJ_CODE = A.DETL_MAJ_CODE AND A.ADMS_NO = H.ADMS_NO AND A.ADMS_NO = I.ADMS_NO AND A.CORS_TYPE_CODE = I.CORS_TYPE_CODE AND A.DEPT_CODE = I.DEPT_CODE AND IFNULL(A.ARI_INST_CODE,'') = IFNULL(I.ARI_INST_CODE,'') AND A.APPL_STS_CODE = '00020'
AND A.ADMS_NO =#{admsNo}
AND F.CAMP_CODE =#{campCode}
AND e.COLL_CODE =#{collCode}
AND b.DEPT_CODE = #{deptCode}
ORDER BY APPL_DATE DESC, APPL_ID DESC

SELECT A.ADMS_NO,D.CAMP_CODE,D.CAMP_NAME, C.COLL_CODE, C.COLL_NAME, B.DEPT_CODE,B.DEPT_NAME, A.APPL_ATTR_CODE,CODEVAL( 'APPL_ATTR',A.APPL_ATTR_CODE) AS APPL_ATTR_NAME,
    COUNT(CASE WHEN CORS_TYPE_CODE IN('01', '05','07','10') THEN 1 END) AS CNT1,
    COUNT(CASE WHEN CORS_TYPE_CODE IN('02', '06','08','11') THEN 1 END) AS CNT2,
    COUNT(CASE WHEN CORS_TYPE_CODE IN('03', '04','12') THEN 1 END) AS CNT3,
    COUNT(CASE WHEN CORS_TYPE_CODE IN('09') THEN 1 END) AS CNT4,
    COUNT(*) AS TOTAL_CNT
FROM APPL A, DEPT B, COLL C, CAMP D WHERE A.DEPT_CODE =B.DEPT_CODE AND B.COLL_CODE= C.COLL_CODE AND C.CAMP_CODE = D.CAMP_CODE AND A.APPL_STS_CODE = '00020'


    AND A.ADMS_NO =#{admsNo}


    AND A.APPL_ATTR_CODE =#{applAttrCode}


    AND D.CAMP_CODE =#{campCode}


    AND C.COLL_CODE =#{collCode}


    AND B.DEPT_CODE = #{deptCode}

GROUP BY A.ADMS_NO,  A.APPL_ATTR_CODE, C.CAMP_CODE, B.COLL_CODE, B.DEPT_CODE
ORDER BY A.ADMS_NO, A.APPL_ATTR_CODE, C.CAMP_CODE, B.COLL_CODE, B.DEPT_SEQ
