mapper.select:

select 

from MDT_ADMS
where ADMS_NO = #{admsNo,jdbcType=VARCHAR}
  and GRP_LEVEL = #{grpLevel,jdbcType=INTEGER}
  and ITEM_CODE_GRP = #{itemCodeGrp,jdbcType=VARCHAR}
  and ITEM_CODE = #{itemCode,jdbcType=VARCHAR}

mapper.delete:

delete from MDT_ADMS
where ADMS_NO = #{admsNo,jdbcType=VARCHAR}
  and GRP_LEVEL = #{grpLevel,jdbcType=INTEGER}
  and ITEM_CODE_GRP = #{itemCodeGrp,jdbcType=VARCHAR}
  and ITEM_CODE = #{itemCode,jdbcType=VARCHAR}
