mapper.select:

select 

from ADMS_MDT
where ADMS_NO = #{admsNo,jdbcType=VARCHAR}
  and FORN_TYPE_CODE = #{fornTypeCode,jdbcType=VARCHAR}
  and ITEM_SEQ = #{itemSeq,jdbcType=INTEGER}

mapper.delete:

delete from ADMS_MDT
where ADMS_NO = #{admsNo,jdbcType=VARCHAR}
  and FORN_TYPE_CODE = #{fornTypeCode,jdbcType=VARCHAR}
  and ITEM_SEQ = #{itemSeq,jdbcType=INTEGER}
