mapper.select:

select 

from APPL_ACAD
where APPL_NO = #{applNo,jdbcType=INTEGER}
  and ACAD_TYPE_CODE = #{acadTypeCode,jdbcType=VARCHAR}
  and ACAD_SEQ = #{acadSeq,jdbcType=INTEGER}

mapper.delete:

delete from APPL_ACAD
where APPL_NO = #{applNo,jdbcType=INTEGER}
  and ACAD_TYPE_CODE = #{acadTypeCode,jdbcType=VARCHAR}
  and ACAD_SEQ = #{acadSeq,jdbcType=INTEGER}
