SELECT CODE_GRP, CODE, CODE_VAL,
UP_CODE_GRP, SEL_GRP_CODE, MDT_YN, CAN_YN, MSG_NO,
LANG_SEQ, TOFL_TYPE_CODE, EXAM_NO, LANG_GRAD, EXAM_DAY, EXPR_DAY,
DOC_SEQ, DOC_GRP, CODEVAL('DOC_ITEM', DOC_ITEM_CODE) DOC_ITEM_NAME, DOC_ITEM_CODE, DOC_NAME
FROM ( SELECT CODE_GRP,CODE, CODE_VAL FROM COMM_CODE WHERE CODE_GRP = #{selGrpCode}
) A LEFT OUTER JOIN ( SELECT BA.ADMS_CORS_NO, BA.DETL_MAJ_CODE, EXAM_CODE_GRP ,EXAM_CODE, UP_CODE_GRP, MDT_YN, MULTI_YN, CAN_YN , MSG_NO, SEL_GRP_CODE FROM ADMS_CORS_MAJ_LANG BA, APPL BB, ADMS_CORS BC WHERE BC.ADMS_CORS_NO = BA.ADMS_CORS_NO AND BB.ADMS_NO =BC.ADMS_NO AND BB.DEPT_CODE = BC.DEPT_CODE AND BB.CORS_TYPE_CODE = BC.CORS_TYPE_CODE AND BB.DETL_MAJ_CODE = BA.DETL_MAJ_CODE
AND BB.APPL_NO = #{applNo} AND BA.UP_CODE_GRP = #{upCodeGrp} AND BA.UP_CODE =#{upCode}
)B ON B.EXAM_CODE = A.CODE LEFT OUTER JOIN ( SELECT LANG_SEQ, LANG_EXAM_CODE, TOFL_TYPE_CODE, EXAM_NO, LANG_GRAD, EXAM_DAY, EXPR_DAY FROM APPL_LANG WHERE APPL_NO = #{applNo}) C ON C.LANG_EXAM_CODE = B.EXAM_CODE LEFT OUTER JOIN ( SELECT DOC_SEQ,DOC_TYPE_CODE, DOC_GRP,DOC_ITEM_NAME, DOC_ITEM_CODE, DOC_NAME FROM APPL_DOC WHERE APPL_NO = #{applNo})D ON D.DOC_TYPE_CODE ='00005' AND C.LANG_SEQ = D.DOC_GRP
