mapper.select:
SELECT B.DETL_MAJ_CODE, C.DETL_MAJ_NAME, B.DETL_MAJ_DESC, B.PART_TIME_YN
FROM ADMS_CORS A, ADMS_CORS_MAJ B, DETL_MAJ C
WHERE A.ADMS_CORS_NO = B.ADMS_CORS_NO
AND B.DETL_MAJ_CODE = C.DETL_MAJ_CODE
AND A.ADMS_NO = #{admsNo,jdbcType=VARCHAR}
AND A.DEPT_CODE = #{deptCode,jdbcType=VARCHAR}
AND A.CORS_TYPE_CODE = #{corsTypeCode,jdbcType=VARCHAR}

SELECT B.DETL_MAJ_CODE, C.DETL_MAJ_NAME, B.DETL_MAJ_DESC, B.PART_TIME_YN
FROM ADMS_CORS A, ADMS_CORS_MAJ B, DETL_MAJ C
WHERE A.ADMS_CORS_NO = B.ADMS_CORS_NO
AND B.DETL_MAJ_CODE = C.DETL_MAJ_CODE
AND A.ADMS_NO = #{admsNo,jdbcType=VARCHAR}
AND A.DEPT_CODE = #{deptCode,jdbcType=VARCHAR}
AND A.ARI_INST_CODE = #{ariInstCode,jdbcType=VARCHAR}
AND A.CORS_TYPE_CODE = #{corsTypeCode,jdbcType=VARCHAR}

select DETL_MAJ_NAME
from DETL_MAJ
where DETL_MAJ_CODE = #{detlMajCode,jdbcType=VARCHAR}
